async function fetchUser(){try{const e=await fetch("/userDetails.json"),t=await e.json();1==localStorage.getItem("loggedIn")&&fillOutDetails(t.user1)}catch(e){console.error("Failed to fetch products data:",e),document.getElementById("errorBox").innerHTML="<p>Failed to load data. Please try again later.</p>"}}function fillOutDetails(e){document.getElementById("username").value=e.name,document.getElementById("address").value=e.postcode,document.getElementById("phone").value=e.phone,document.getElementById("email").value=e.email}fetchUser(),document.getElementById("orderBtn").addEventListener("click",(()=>{localStorage.getItem("loggedIn");localStorage.clear(),localStorage.setItem("loggedIn","loggedIn")}));