async function fetchUser(){try{const e=await fetch("/userDetails.json");return(await e.json()).user1}catch(e){console.error("Failed to fetch products data:",e),document.getElementById("errorBox").innerHTML="<p>Failed to load data. Please try again later.</p>"}}document.getElementById("login").addEventListener("click",(async()=>{const e=await fetchUser();document.getElementById("password").value==e.password&&document.getElementById("name").value==e.name?localStorage.setItem("loggedIn","1"):localStorage.setItem("loggedIn","0"),location.reload()}));