async function fetchUser(){try{const e=await fetch("/userDetails.json"),t=await e.json();"1"==localStorage.getItem("loggedIn")&&fillOutDetails(t.user1)}catch(e){console.error("Failed to fetch products data:",e),document.getElementById("errorBox").innerHTML="<p>Failed to load data. Please try again later.</p>"}}function fillOutDetails(e){document.getElementById("name").textContent=e.name,document.getElementById("address").textContent=e.postcode,document.getElementById("phone").textContent=e.phone,document.getElementById("email").textContent=e.email}fetchUser();